import { Component } from "react";
import { nanoid } from "nanoid";
import ContactForm from "./ContactForm/ContactForm";
import ContactList from "./ContactList/ContactList";
import Filter from "./Filter/Filter";
import Notiflix from "notiflix";
import { Notify } from "notiflix";
export class App extends Component {
  loginInputId = nanoid();

  state = {
    contacts: [
      { id: "id-1", name: "Rosie Simpson", number: "459-12-56" },
      { id: "id-2", name: "Hermione Kline", number: "443-89-12" },
      { id: "id-3", name: "Eden Clements", number: "645-17-79" },
      { id: "id-4", name: "Annie Copeland", number: "227-91-26" },
    ],
    filter: "",
  };
  handelChangeName = (e) => {
    this.setState({
      name: e.target.value,
    });
  };
  handelChangeTel = (e) => {
    this.setState({
      number: e.target.value,
    });
  };
  saveChange = (e) => {
    let cont = this.state.contacts;
    for (let i = 0; i < cont.length; i++) {
      console.log(e.currentTarget.value);
      if (cont[i].name === e.target.value) {
        return Notiflix.Notify.info(`${e.target.value} is already is contacts`);
      }
    }
    // for (let i = 0; i < this.state.contacts.length; i++) {
    //   console.log(name[i]);
    //   if (name[i] === e.target.value) {
    //     Notiflix.Notify.info(`${name} is already is contacts`);
    //   }
    // }
    // console.log(name);
    console.log(cont);
    cont.push({
      id: this.loginInputId,
      name: this.state.name,
      number: this.state.number,
    });
    return this.setState({
      contacts: cont,
      name: "",
      number: "",
    });
  };
  handleFilter = (e) => {
    this.setState({
      filter: e.target.value,
    });
  };
  filterName = (name) => {
    return name.toLowerCase().indexOf(this.state.filter.toLowerCase()) !== -1;
  };
  render() {
    const contacts = this.state.contacts;
    const filter = this.state.filter;
    return (
      <div>
        <h1>PhoneBook</h1>
        <ContactForm
          handelChangeName={this.handelChangeName}
          handelChangeTel={this.handelChangeTel}
          name={contacts}
          tel={contacts}
          saveChange={this.saveChange}
        />

        <h2>Contacts</h2>
        <Filter filter={filter} handelFilter={this.handleFilter} />
        <ContactList contacts={contacts} filterName={this.filterName} />
      </div>
    );
  }
}
