import { Component } from "react";
import ContactForm from "./ContactForm/ContactForm";
export class App extends Component {
  state = {
    contacts: [],
    name: "",
  };

  // handelChange = (e) => {
  //   this.setState({ value: e.target.value });
  // };
  handelChange = (e) => {
    this.setState({
      name: e.target.value,
    });
  };
  render() {
    const { value } = this.state.name;
    return (
      <div>
        <h1>PhoneBook</h1>
        <ContactForm handelChange={this.handelChange} value={value} />
        <h2>Contacts</h2>
      </div>
    );
  }
}
